var ready=function(){$(".start-conversation").click(function(t){t.preventDefault();var a=$(this).data("sid"),c=$(this).data("rip");$.post("/conversations",{sender_id:a,recipient_id:c},function(t){chatBox.chatWith(t.conversation_id)})}),$(document).on("click",".toggleChatBox",function(t){t.preventDefault();var a=$(this).data("cid");chatBox.toggleChatBoxGrowth(a)}),$(document).on("click",".closeChat",function(t){t.preventDefault();var a=$(this).data("cid");chatBox.close(a)}),$(document).on("keydown",".chatboxtextarea",function(t){var a=$(this).data("cid");chatBox.checkInputKey(t,$(this),a)}),$("a.conversation").click(function(t){t.preventDefault();var a=$(this).data("cid");chatBox.chatWith(a)})};$(document).ready(ready),$(document).on("page:load",ready);